import React, { useState, useEffect, useContext } from "react";
import { BsList, BsFillCaretDownFill } from "react-icons/bs";
import { FaSearch, FaUser } from "react-icons/fa";
import "./header.scss";
import { ProductContext } from "../../Context/ProductContext";

const Header = () => {
    const {setSearch}=useContext(ProductContext)
    const [values ,setValues]  = useState()
    const handleSumbitSearch = ()=>{
      setSearch(values)
}


    
  return (
    <section>
      <div className="c-header">
        <div className="c-header__col1">
          <h1 className="c-header__headertitle">Quản Lý Bán Hàng</h1>
          <BsList className="c-header__icon" />
        </div>
        <div className="c-header__col2">
          <div className="c-header__search">
            <input
              type="text"
              placeholder="search for..."
              className="c-header__input"
              onChange={(e)=>setValues(e.target.value)}
            />
            <div style={{ padding: "6px 12px", backgroundColor: "#0d6ffd" }} onClick={handleSumbitSearch}>
              <FaSearch className="c-header__icon" />
            </div>
          </div>
          <div>
            <FaUser className="c-header__icon" />
            <BsFillCaretDownFill className="c-header__icon" />
          </div>
        </div>
      </div>
    </section>
  );
};

export default Header;
