import React from 'react'
import axios from 'axios';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import API from '../../api/apiConfig'
export const  ModalDelete=(props)=> {
    const {open, setOpen, id , products, setProducts}=props;
    const handleClose = () => {
        setOpen(false);
      };
    const handleDelete=()=>{
      API.delete(`api/product/${id}`)
      .then(res=>{
        alert('susscess')
        setProducts(
            products.filter((item)=>{
                return item.id !== id ;
            })
        )
      })
      .catch(err=>{console.log(err)})
        setOpen(false);

        
    }
     
      
  return (
    <div  className='c-Modal'>
    <Dialog
    open={open}
    onClose={handleClose}
    aria-labelledby="alert-dialog-title"
    aria-describedby="alert-dialog-description"
    BackdropProps={{ invisible: true }}
    PaperProps={{
        elevation: 0,
        sx: {
          border: 'none',
          width:'900px',
          height:'200px'
          
        }
      }}
  
  >
    <DialogTitle id="alert-dialog-title">
      {" Bạn có chắc chắc muốn Xóa sản phẩm này không"}
    </DialogTitle>
    <DialogContent>
      <DialogContentText id="alert-dialog-description">
        Bạn không thể khôi phục lại được sản phẩm sau khi Xóa
      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <Button onClick={handleClose}>Quay Lại</Button>
      <Button onClick={handleDelete} autoFocus >
        Đồng Ý
      </Button>
    </DialogActions>
  </Dialog>
    </div>
    
  )
}

//  Add product
export const  ModalAdd=(props)=> {
    const {open, setOpen}=props;
    const handleClose = () => {
        setOpen(false);
      };
  return (
    <div  className='c-Modal'>
    <Dialog
    open={open}
    onClose={handleClose}
    aria-labelledby="alert-dialog-title"
    aria-describedby="alert-dialog-description"
    BackdropProps={{ invisible: true }}
    PaperProps={{
        elevation: 0,
        sx: {
          border: 'none',
        
          
        }
      }}
  
  >
    <DialogTitle id="alert-dialog-title">
      {" Bạn có chắc chắc muốn Xóa sản phẩm này không"}
    </DialogTitle>
    <DialogContent>
      <DialogContentText id="alert-dialog-description">
        Bạn thực sự muốn thêm sản phẩm mới
      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <Button onClick={handleClose}>Quay Lại</Button>
      <Button onClick={handleClose} autoFocus>
        Đồng Ý
      </Button>
    </DialogActions>
  </Dialog>
    </div>
    
  )
}
