import React, {useState,useEffect} from 'react'
import Input from '../Input/Input'
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import API from '../../api/apiConfig'
import { createGrid } from '@mui/system';


export const  ModalDelete=(props)=> {
    const {open, setOpen, id , products, setProducts}=props;
   
    const handleClose = () => {
        setOpen(false);
      };
    const handleDelete=()=>{
      API.delete(`api/product/${id}`)
      .then(res=>{
        alert('susscess')
        // setProducts(
        //     products.filter((item)=>{
        //         return item.id !== id ;
        //     })
        // )
      })
      .catch(err=>{console.log(err)})
        setOpen(false);

        
    }
     
      
  return (
    <div  className='c-Modal'>
    <Dialog
    open={open}
    onClose={handleClose}
    aria-labelledby="alert-dialog-title"
    aria-describedby="alert-dialog-description"
    BackdropProps={{ invisible: true }}
    PaperProps={{
        elevation: 0,
        sx: {
          border: 'none',
          width:'900px',
          height:'200px'
          
        }
      }}
  
  >
    <DialogTitle id="alert-dialog-title">
      <span>Bạn có chắc chắc muốn Xóa sản phẩm này không ?</span>
    </DialogTitle>
    <DialogContent>
      <DialogContentText id="alert-dialog-description">
        <span>Bạn không thể khôi phục lại được sản phẩm sau khi Xóa</span>
      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <Button onClick={handleClose}><h3>Quay Lại</h3></Button>
      <Button onClick={handleDelete} autoFocus >
      <span>  Đồng Ý</span>
      </Button>
    </DialogActions>
  </Dialog>
    </div>
    
  )
}

//  Add product
export const  ModalAdd=(props)=> {
    const {open, setOpen}=props;
    const [item,setItem] = useState({
        title:'',
        desc:'',
        firstimg:'',
        secondimg:'',
        categories:'',
        price:''
    })
    const handleClose = () => {
        setOpen(false);
      };
    
    //change Input 
     const handleOnchangeInput = (event,id)=>{
      let copyItem = {...item}
      copyItem[id]= event.target.value;
      setItem({
        ...copyItem
      })
      console.log(item)
       
     }
     // add
     const handleAddProduct = (item)=>{
       API.post(`/api/product`,item)
       .then(res=>{
         alert('Add Successful')
       })
       .catch(err=>{console.log(err)})
        setOpen(false);
     }
  return (
    <div  className='c-Modal'>
    <Dialog
    open={open}
    onClose={handleClose}
    aria-labelledby="alert-dialog-title"
    aria-describedby="alert-dialog-description"
    BackdropProps={{ invisible: true }}
    PaperProps={{
        elevation: 0,
        sx: {
          border: 'none',
          width:'900px'
        
          
        }
      }}
  
  >
    <DialogTitle id="alert-dialog-title">
     <span>Thêm Sản Phẩm</span>
    </DialogTitle>
    <DialogContent>
      <DialogContentText id="alert-dialog-description">
       <Input type="text" name="Title"  onChange={(event)=>{handleOnchangeInput(event,"title")}}/>
       <Input type="text" name="Desc"    onChange={(event)=>{handleOnchangeInput(event,"desc")}}/>
       <Input type="text" name="Firstimg"  onChange={(event)=>{handleOnchangeInput(event,"firstimg")}}/>
       <Input type="text" name="Secondimg"  onChange={(event)=>{handleOnchangeInput(event,"secondimg")}}/>
       <Input type="text" name="Categories"  onChange={(event)=>{handleOnchangeInput(event,"categories")}}/>
       <Input type="text" name="Price"  onChange={(event)=>{handleOnchangeInput(event,"price")}}/>


      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <Button onClick={handleClose}><span>Quay Lại</span></Button>
      <Button onClick={()=>{handleAddProduct(item)}} autoFocus>
       <span> Đồng Ý</span>
      </Button>
    </DialogActions>
  </Dialog>
    </div>
    
  )
}


// Edit Product 
