import React, { useState,useEffect} from 'react'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import API from '../../api/apiConfig'
import './product.scss'

const Product = () => {
    const [products,setProducts]= useState([])
    
  useEffect(()=>{
    API.get(`api/product`)
    .then((res)=>{
       const product = res.data;
      console.log(product)
      setProducts(product)
    })
  },[])
  return (
    <section className='c-Product'>
    <TableContainer component={Paper}>
    <Table sx={{ width:'100%' }} aria-label="simple table">
      <TableHead>
        <TableRow>
          <TableCell><h1 className='c-Product__title'>Tên</h1></TableCell>
          <TableCell align="center"><h1 className='c-Product__title'>Loại</h1></TableCell>
          <TableCell align="center"><h1 className='c-Product__title'>Giá</h1></TableCell>
          <TableCell align="center"><h1 className='c-Product__title'>Ngày Nhập Hàng</h1></TableCell>
          <TableCell align="center"><h1 className='c-Product__title'>Số lượng</h1></TableCell>
        </TableRow>
      </TableHead>
      <TableBody>
      {/* */}
        {products.map((row) => (
          <TableRow
            key={row.id}
            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
          >
            <TableCell component="th" scope="row">
             <span className="c-Product__text">{row.title}</span> 
            </TableCell>
            <TableCell align="center"><span className="c-Product__text">{row.categories}</span> </TableCell>
            <TableCell align="center"><span className="c-Product__text"> {row.price}</span></TableCell>
            <TableCell align="center"><span className="c-Product__text">{row.createdAt}</span> </TableCell>
            <TableCell align="center"><span className="c-Product__text">{row.inStock ? "còn hàng" : "hết hàng"}</span> </TableCell>
          </TableRow>
        ))}
      </TableBody>
    </Table>
  </TableContainer>
    
    </section>
  )
}

export default Product
