import React,{useState,useEffect} from 'react'
import { useParams } from 'react-router-dom'
import API from "../../api/apiConfig"
const UserDetail = () => {
  let {id} = useParams();
  const [user,setUser] = useState({});
  const getAnUser = ()=>{
    API.get(`api/user/${id}`)
    .then((res)=>{
      const newUser = res.data;
      setUser(newUser)
    })
  }
  useEffect(()=>{
    getAnUser();
  },[])
  
  return (
    <section className='c-UserDetail' >
      <div className='c-UserDetail__content'></div>
    <div className='c-UserDetail__img'></div>
    
    </section>
  )
}

export default UserDetail
