import React, {useState,useEffect} from 'react'
import Input from '../Input/Input'
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import API from '../../api/apiConfig'


export const  ModalDelete=(props)=> {
    const {open, setOpen, id , products, setProducts}=props;
    const handleClose = () => {
        setOpen(false);
      };
    const handleDelete=()=>{
      API.delete(`api/product/${id}`)
      .then(res=>{
        alert('susscess')
        setProducts(
            products.filter((item)=>{
                return item.id !== id ;
            })
        )
      })
      .catch(err=>{console.log(err)})
        setOpen(false);

        
    }
     
      
  return (
    <div  className='c-Modal'>
    <Dialog
    open={open}
    onClose={handleClose}
    aria-labelledby="alert-dialog-title"
    aria-describedby="alert-dialog-description"
    BackdropProps={{ invisible: true }}
    PaperProps={{
        elevation: 0,
        sx: {
          border: 'none',
          width:'900px',
          height:'200px'
          
        }
      }}
  
  >
    <DialogTitle id="alert-dialog-title">
      <h1>Bạn có chắc chắc muốn Xóa sản phẩm này không</h1>
    </DialogTitle>
    <DialogContent>
      <DialogContentText id="alert-dialog-description">
        <h2>Bạn không thể khôi phục lại được sản phẩm sau khi Xóa</h2>
      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <Button onClick={handleClose}><h3>Quay Lại</h3></Button>
      <Button onClick={handleDelete} autoFocus >
      <h3>  Đồng Ý</h3>
      </Button>
    </DialogActions>
  </Dialog>
    </div>
    
  )
}

//  Add product
export const  ModalAdd=(props)=> {
    const {open, setOpen}=props;
    const handleClose = () => {
        setOpen(false);
      };
  return (
    <div  className='c-Modal'>
    <Dialog
    open={open}
    onClose={handleClose}
    aria-labelledby="alert-dialog-title"
    aria-describedby="alert-dialog-description"
    BackdropProps={{ invisible: true }}
    PaperProps={{
        elevation: 0,
        sx: {
          border: 'none',
          width:'900px'
        
          
        }
      }}
  
  >
    <DialogTitle id="alert-dialog-title">
     <h1>Thêm Sản Phẩm</h1>
    </DialogTitle>
    <DialogContent>
      <DialogContentText id="alert-dialog-description">
       <Input type="text" name="Name"/>
       <Input type="text" name="Desc"/>
       <Input type="text" name="Firstimg"/>
       <Input type="text" name="Secondimg"/>
       <Input type="text" name="Categories"/>
       <Input type="text" name="Price"/>


      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <Button onClick={handleClose}><h2>Quay Lại</h2></Button>
      <Button onClick={handleClose} autoFocus>
       <h2> Đồng Ý</h2>
      </Button>
    </DialogActions>
  </Dialog>
    </div>
    
  )
}
