import React from 'react';
import './input.scss';
const Input = (props) => {
       const {type,name} = props;
  return (
    <div class="form__group field">
     <input className="form__field" type={type} name={name}/>
     <label for={name} className="form__label">{name}</label>
    </div>
  )
}

export default Input
