        setOpenModalEdit(false);
      };
    
    //change Input 
     const handleOnchangeInput = (event,id)=>{
      let copyItem = {...item}
      copyItem[id]= event.target.value;
      setItem({
        ...copyItem
      })
       
     }
     // edit
     const handleEditProduct = (item)=>{
        
   
       setOpenModalEdit(false);

     }
    
  return (
   
    <Dialog
    open={openModalEdit}
    onClose={handleClose}
    aria-labelledby="alert-dialog-title"
    aria-describedby="alert-dialog-description"
    BackdropProps={{ invisible: true }}
    PaperProps={{
        elevation: 0,
        sx: {
          border: 'none',
          width:'900px'
        
          
        }
      }}
  
  >
    <DialogTitle id="alert-dialog-title">
     <span>Sửa Sản Phẩm</span>
    </DialogTitle>
    <DialogContent>
      <DialogContentText id="alert-dialog-description">
        
       <Input type="text" name="Title"   onChange={(event)=>{handleOnchangeInput(event,"title")}}/>
       <Input type="text" name="Desc"    onChange={(event)=>{handleOnchangeInput(event,"desc")}}/>
       <img  alt="no results" style={{width:"90px" ,height:"90px"}}/>
       <Input type="file" name="Firstimg"  onChange={(event)=>{handleOnchangeInput(event,"firstimg")}}/>
       <img  alt="no results" style={{width:"90px" ,height:"90px"}}/>
       <Input type="file" name="Secondimg"  onChange={(event)=>{handleOnchangeInput(event,"secondimg")}}/>
       <Input type="text" name="Categories"  onChange={(event)=>{handleOnchangeInput(event,"categories")}}/>
       <Input type="text" name="Price"  onChange={(event)=>{handleOnchangeInput(event,"price")}}/>
       <Input type="text" name="Price"   />
     
      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <Button onClick={handleClose}><span>Quay Lại</span></Button>
      <Button onClick={()=>{handleEditProduct(item )}} autoFocus>
       <span> Đồng Ý</span>
      </Button>
    </DialogActions>
  </Dialog>

    
  )
}
