import React,{useState} from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./newproduct.scss";
import Slider from "react-slick";
import {GrFormNext,GrFormPrevious} from "react-icons/gr"

import { Product } from "../../.././data";
const customeSlider = React.createRef();

const NewProduct = () => {
  const [open , setOpen]=useState('');
  const unhover = ()=>{
         setOpen('');
  }
  var settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 2,
   
  
  };
  const gotoNext = () => {
    customeSlider.current.slickNext()
  }

  const gotoPrev = () => {
    customeSlider.current.slickPrev()
  }
  return (
    <div className="section__newproduct">
      <div className="title">
        <h3 style={{fontFamily:'Barlow Semi Condensed, sans-serif',fontSize:"2.4rem",fontWeight:"500",marginBottom:"20px"}}>New items</h3>
        
        <span className="title__divider"></span>
      </div>
      <div className="newproduct__btn">
    
      <span onClick={()=>gotoNext()} className="newproduct__btn--next"><GrFormPrevious/></span>
      <span onClick={()=>gotoPrev()} className="newproduct__btn--prev"><GrFormNext/></span>
      </div>
   
      <Slider {...settings} ref={customeSlider}>
        {Product.map((products) => (
          <div className="items">
          {products.sale==='sale!'?(
            <span className="items--sale">{products.sale}</span>
          ):null}
            <div className="items__img" >
              <img onMouseOver={()=>setOpen('lastimage')} onMouseOut={unhover} src={open==='lastimage'?products.lastimg:products.firstimg} alt="Glass vase" width='100%' />
            </div>
            <div className="items__contents">
              <h1 className="items__contents--title">{products.title}</h1>
              <span className="items__contents--price">${products.price}</span>
            </div>
          </div>
        ))}
      </Slider>
     
    </div>
  );
};

export default NewProduct;
