import React from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./newproduct.scss";
import Slider from "react-slick";

import { Product } from "../../.././data";
const customeSlider = React.creatRef();

const NewProduct = () => {
  var settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 2,
   
  
  };
  const gotoNext = () => {
    customeSlider.current.slickNext()
  }

  const gotoPrev = () => {
    customeSlider.current.slickPrev()
  }
  return (
    <div className="section__newproduct">
    <button onClick={()=>gotoNext()}><span>Next</span></button>
    <button onClick={()=>gotoPrev()}>Previous</button>
      <div className="title">
        <h3 style={{fontFamily:'Barlow Semi Condensed, sans-serif',fontSize:"2.4rem",fontWeight:"500",marginBottom:"20px"}}>New items</h3>
        
        <span className="title__divider"></span>
      </div>
      <Slider {...settings}>
        {Product.map((products) => (
          <div className="items">
          {products.sale==='sale!'?(
            <span className="items--sale">{products.sale}</span>
          ):null}
            <div className="items__img">
              <img src={products.firstimg} alt="Glass vase" width='100%' />
            </div>
            <div className="items__contents">
              <h1 className="items__contents--title">{products.title}</h1>
              <span className="items__contents--price">${products.price}</span>
            </div>
          </div>
        ))}
      </Slider>
    
    </div>
  );
};

export default NewProduct;
