import React from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { Category } from "../../.././data";
import "./Categories.jsx"
const Categories = () => {
  return (
    <div className="categories--row">
      <div className="categories--column1">
        <span>Categories</span>
      </div>
      <div className="categories--column2">
      {Category.map((categories) => (
        <div key={categories.id} className="items">
          <div className="items--img">
            <img src={categories.img} alt="textitle & decor" width="100%" height="100%"/>
          </div>
          <div>
            <h3>{categories.title}</h3>
            <p>{categories.quantity}</p>
          </div>
        </div>
      ))}
      </div>
      
    </div>
  );
};

export default Categories;
