import React, { useState, useRef } from "react";
import "./catalogue.scss";
import { HiOutlineChevronDown, HiOutlineChevronUp } from "react-icons/hi";
import { Product } from "./../../../data";
import * as Bs from "react-icons/bs";
import * as Fa from "react-icons/fa";
import * as Gr from "react-icons/gr"
const Catalogue = () => {
  // isChev
  const [isChevCategory, setisChevCategory] = useState(false);
  const [isChevColor, setischevColor] = useState(false);
  const [isChevSize,setisChevSize] = useState(false);


  //isPlusLg
  const [Apparel, setApparel] = useState(false);
  const [decor, setDecor] = useState(false);

 //useRef
  const openCategory = useRef(null);
  const openColor =useRef(null);
  const openSize = useRef(null);
  const openApparel = useRef(null);
  const openDecor = useRef(null);

 
  const handleChev = () => {
    setisChevCategory(!isChevCategory);
    if (!isChevCategory) {
      openCategory.current.classList.add("active");
    } else {
      openCategory.current.classList.remove("active");
      openApparel.current.classList.remove("active");
    }
  };
  const handleColor = () => {
    setischevColor(!isChevColor);
    if (!isChevColor) {
      openColor.current.classList.add("active");
    } else {
      openColor.current.classList.remove("active");
   
    }
   
  };
  const handleSize = () => {
    setisChevSize(!isChevSize);
    if (!isChevSize) {
      openSize.current.classList.add("active");
    } else {
      openSize.current.classList.remove("active");
   
    }
   
  };
  const handleApparel = () => {
    setApparel(!Apparel);
    if (!Apparel) {
      openApparel.current.classList.add("active");
    } else {
      openApparel.current.classList.remove("active");
    }
  };

  const handleDecor = () => {
    setDecor(!decor);
    if (!decor) {
      openDecor.current.classList.add("active");
    } else {
      openDecor.current.classList.remove("active");
    }
  };


  return (
    <div>
      <div className="c-catalogue">
        <div className="c-catalogue__left">
          {/* start Category*/}
          <div className="filter">
            <div>
              <h2 className="filter__title">Category</h2>
            </div>
            <div>
              {isChevCategory ? (
                <HiOutlineChevronUp
                  className="filter__icon"
                  onClick={handleChev}
                />
              ) : (
                <HiOutlineChevronDown
                  className="filter__icon"
                  onClick={handleChev}
                />
              )}
            </div>
          </div>
          <ul ref={openCategory} className="list">
            {/* start li-1 */}
            <li>
              <p> Apparel</p>
              <span className="count">9</span>
              {Apparel ? (
                <Fa.FaMinus className="next-list" onClick={handleApparel} />
              ) : (
                <Bs.BsPlusLg className="next-list" onClick={handleApparel} />
              )}
            </li>
            <ul ref={openApparel} className="list-item">
              <li>
                <p>Coats & jackets</p>
                <span className="count">3</span>
              </li>
              <li>
                <p>Dresses</p>
                <span className="count">1</span>
              </li>
              <li>
                <p>Knitwear</p>
                <span className="count">2</span>
              </li>
              <li>
                <p>Suits</p>
                <span className="count">4</span>
              </li>
            </ul>

            {/* end li-1 */}

            {/* start li-2*/}
            <li>
              Home decor
              <span className="count">15</span>
              {decor ? (
                <Fa.FaMinus className="next-list" onClick={handleDecor} />
              ) : (
                <Bs.BsPlusLg className="next-list" onClick={handleDecor} />
              )}
            </li>
            <ul ref={openDecor} className="list-item">
              <li>
                <p>Furniture</p>
                <span className="count">5</span>
              </li>
              <li>
                <p>Lighting</p>
                <span className="count">5</span>
              </li>
              <li>
                <p>Textile & decor</p>
                <span className="count">7</span>
              </li>
            </ul>
            {/* end li-2 */}

            {/*start li-3*/}
            <li>
              Uncategorized <span className="count">0</span>
            </li>
            {/*end li-3*/}
          </ul>
          {/* end Category*/}
          
          
      <div className="widgets">
      <div className="widgets__divider">
        <span className="widgets__divider--separator"></span>
      </div>
    </div>

          {/* start color*/ }
          <div className="filter">
          <div>
            <h2 className="filter__title">Color</h2>
          </div>
          <div>
            {isChevColor ? (
              <HiOutlineChevronUp
                className="filter__icon"
                onClick={handleColor}
              />
            ) : (
              <HiOutlineChevronDown
                className="filter__icon"
                onClick={handleColor}
              />
            )}
          </div>
        </div>
        <ul ref={openColor} className="list list-Color">
        <li>
        <div><Gr.GrCheckbox/></div><p>White</p><span className="count">5</span>
        </li>
        <li>
        <div><Gr.GrCheckbox/></div> <p>Black</p><span className="count">9</span>
        </li>
        <li>
        <div><Gr.GrCheckbox/></div> <p>Brown</p><span className="count">3</span>
        </li>
        <li>
        <div><Gr.GrCheckbox/></div><p>Beige</p><span className="count">3</span>
        </li>
        <li>
        <div><Gr.GrCheckbox/></div><p>Gray</p><span className="count">6</span>
        </li>
        <li>
        <div><Gr.GrCheckbox/></div> <p>Neutral</p><span className="count">5</span>
        </li>
        <li>
        <div><Gr.GrCheckbox/></div><p>Red</p><span className="count">3</span>
        </li>
        <li>
        <div><Gr.GrCheckbox/></div><p>Yellow</p><span className="count">1</span>
        </li>
        </ul>
            {/* end color*/ }

            <div className="widgets">
            <div className="widgets__divider">
              <span className="widgets__divider--separator"></span>
            </div>
          </div>
        
            {/* end Size*/ }
          <div className="filter">
          <div>
            <h2 className="filter__title">Size</h2>
          </div>
          <div>
            {isChevSize ? (
              <HiOutlineChevronUp
                className="filter__icon"
                onClick={handleSize}
              />
            ) : (
              <HiOutlineChevronDown
                className="filter__icon"
                onClick={handleSize}
              />
            )}
          </div>
        </div>
        <div className="Size">
         <div>One size</div>
         <div>XS</div>
         <div>S</div>
         <div>M</div>
         <div>L</div>
         <div>XL</div>

        </div>



        </div>
        <div className="c-catalogue__right">
          <div className="c-catalogue-category">
            <span>Products</span>
          </div>
          <div className="c-catalogue__list">
            {Product.map((item) => (
              <div className="c-catalogue__item" key={item.id}>
                <div className="c-catalogue__content">
                  <figure className="c-catalogue__img">
                    <img src={item.firstimg} alt="no results" />
                  </figure>
                  <div className="c-catalogue__info">
                    <h4 className="c-catalogue__title">{item.title}</h4>
                    <p className="c-catalogue__price">${item.price}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};
export default Catalogue;
